CodeSet.SupportCode:


final BigInteger[] validValues = {new BigInteger("-88651049463528008853"), new BigInteger("-8865104946352800885"), 
                                  new BigInteger("-1")};
final BigInteger[] invalidValues = {new BigInteger("-886510494635280088530"), new BigInteger("-88651049463528008854"), 
                                    new BigInteger("0"), new BigInteger("88651049463528008854")};

JAXBElement<BigInteger> elemObj;

Status testInvalid() {
    for (int i = 0; i < invalidValues.length; ++i){

            elemObj.setValue(invalidValues[i]);
            Status status = validate(elemObj);
            if (status.isPassed()) {
                return Status.failed("" + invalidValues[i] + " is unexpectedly valid");
            }

    }
    return Status.passed("OK");
}

Status testValid() {
    for (int i = 0; i < validValues.length; ++i){
        elemObj.setValue(validValues[i]);
        Status status = validate(elemObj);
        if (!status.isPassed()){
            return Status.failed("" + validValues[i] + " is unexpectedly invalid");
        }
    }
    return Status.passed("OKAY");
}
